package io.khw.domain.popularsearchkeyword.entity;import jakarta.persistence.*;import lombok.*;import lombok.experimental.SuperBuilder;import org.hibernate.annotations.DynamicInsert;import org.springframework.data.jpa.domain.support.AuditingEntityListener;import java.io.Serializable;@NoArgsConstructor@Getter@Table(name = "popular_search_keyword")@Entity@DynamicInsertpublic class PopularSearchKeywordEntity implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "id")    private Long id;    @Column(name = "keyword", unique = true, nullable = false, columnDefinition = "varchar (255) NOT NULL COMMENT '검색 키워드'")    private String keyword;    @Column(name = "search_volume", nullable = false, columnDefinition = "BIGINT COMMENT '키워드 검색 횟수'")    private long searchVolume;    private PopularSearchKeywordEntity(String keyword, long searchVolume){        this.keyword = keyword;        this.searchVolume = searchVolume;    }        public static PopularSearchKeywordEntity createPopularSearchKeyWord(String keyword, long searchVolume){        return new PopularSearchKeywordEntity(keyword, searchVolume);    }}