package io.khw.domain.popularsearchkeyword.repository;import io.khw.domain.popularsearchkeyword.entity.PopularSearchKeywordEntity;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import java.util.List;import java.util.Optional;@Repositorypublic interface PopularSearchKeywordRepository extends JpaRepository<PopularSearchKeywordEntity, Long> {    Optional<PopularSearchKeywordEntity> findByKeyword(String keyword);    List<PopularSearchKeywordEntity> findTop10ByOrderBySearchVolumeDesc();    @Modifying    @Query("UPDATE PopularSearchKeywordEntity p SET p.searchVolume = p.searchVolume + 1 WHERE p.keyword = :keyword")    int incrementSearchVolumeByKeyword(@Param("keyword") String keyword);}